export class DataDisplayComponent {
  constructor(dataService, http, dialog) {
    this.dataService = dataService;
    this.http = http;
    this.dialog = dialog;
    this.pageSize = 5; //Número de itens por página

    this.pageIndex = 0; //Indice da página atual

    this.lengthSupp = 0; //Total de itens de cda_supp e contribuinte_supp

    this.lengthSiatu = 0; //Total de itens de cda_siatu e contribuinte_siatu
    //Para mostrar/esconder partes dos dados

    this.showCdaSupp = true;
    this.showContribuinteSupp = true;
    this.showCdaSiatu = true;
    this.showContribuinteSiatu = true;
  }

  ngOnInit() {
    console.log('DataDisplayComponent initialized');
    this.data$ = this.dataService.getData(); // Buscar os dados da API
    // Aqui você pode definir a lógica para contar o número total de itens
    // Se os dados estiverem carregados

    this.data$.subscribe(data => {
      if (data) {
        this.lengthSupp = (data.cda_supp.length || 0) + (data.contribuinte_supp.length || 0);
        this.lengthSiatu = (data.cda_siatu.length || 0) + (data.contribuinte_siatu.length || 0);
      }
    });
  }

  onPageChange(event) {
    this.pageIndex = event.pageIndex;
    this.pageSize = event.pageSize;
  } //Método para abrir o modal com o PDF


  openPdfModal(id) {
    const pdfUrl = `http://localhost:8000/export/pdf/${id}`; //Faz a requisição para buscar a string base64 do PDF

    this.http.get(pdfUrl).subscribe(response => {
      const pdfBase64 = response.pdfBase64; //Converte a string Base64 para Blob

      const blob = this.base64ToBlob(pdfBase64, 'application/pdf');
      const pdfBlobUrl = URL.createObjectURL(blob); //Abre o modal e exibe o PDF

      this.dialog.open(PdfModalComponent, {
        width: '80%',
        height: '80%',
        data: {
          pdfUrl: pdfBlobUrl
        } //Passa a URL do PDF para o modal

      });
    });
  } //Método para converter a string Base64 para um Blob


  base64ToBlob(base64, contentType) {
    const byteCharacters = atob(base64); //Decodifica a string Base64

    const byteNumbers = new Array(byteCharacters.length);

    for (let i = 0; i < byteCharacters.length; i++) {
      byteNumbers[i] = byteCharacters.charCodeAt(i);
    }

    const byteArray = new Uint8Array(byteNumbers);
    return new Blob([byteArray], {
      type: contentType
    });
  }

}

